import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {name : 'Max', age:28},
      {name: 'Manu', age:29},
      {name: 'Stephanie', age:26},
    ],
    otherState: 'some other value',
    showPersons: false
  }

  switchNameHandler = (newName) => {
    //console.log('Was clicked!');
    //DONT DO THIS: this.state.persons[0].name = 'Francisco';
    this.setState({persons: [
      {name: newName, age:22},
      {name: 'Andres', age:19},
      {name: 'Leonardo', age:16}
    ]})
  }

  togglePersonHandler = () => {

  }

  nameChangedHandler = (event) => {
    this.setState({
      persons: [
        {name: 'Francisco', age:22},
        {name: event.target.value, age: 19},
        {name: 'Leonardo', age:16}
      ]
    })
  }

  render() {
    const style = {
      backgroundColor: 'white',
      font: 'inheret',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    return (
      <div className="App">
      <h1> Hi, World!</h1>
      <p> THis is really working! </p>
      <button
        style={style}
        onClick={this.togglePersonHandler}> Switch Name </button>
        { this.state.showPersons ? 
            <div>
            <Person
              name={this.state.persons[0].name}
              age={this.state.persons[0].age}/>
            <Person
              name={this.state.persons[1].name}
              age={this.state.persons[1].age}
              //USE THE BIND SYNTEX INSTEAD ITS BETTER
              click={this.switchNameHandler.bind(this,'Kikin!!!!!')}
              changed={this.nameChangedHandler}> My Hobbies: League of Legends </Person>
            <Person
              name={this.state.persons[2].name}
              age={this.state.persons[2].age} />
        </div> : null
      }
      </div>
    );
    //return React.createElement('div', {className: ' App'}, React.createElement('h1', null, 'Does this work now?'))
  }
}

export default App;
